// Configuration WIDIP Local RAG
const input = $input.first();
let config = {
  mode: 'incremental',
  clearFirst: false,
  sourcePath: '/home/node/.n8n-files/widip-docs',
  triggerType: 'manual',
  startedAt: new Date().toISOString(),
  supportedExtensions: ['.md', '.txt', '.pdf', '.docx', '.doc', '.xlsx', '.xls', '.csv', '.json', '.html', '.htm', '.py', '.ts', '.js'],
  customCategory: null,
  conversationId: null,
  userId: null
};

// Detecter le type de trigger
if (input.json.headers) {
  config.triggerType = 'webhook';
  const body = input.json.body || {};
  config.mode = body.mode || 'incremental';
  config.clearFirst = body.clear === true;
  if (body.path) config.sourcePath = body.path;
  if (body.category) config.customCategory = body.category;
  if (body.conversation_id) config.conversationId = body.conversation_id;
  if (body.user_id) config.userId = body.user_id;
} else if (input.json.timestamp) {
  config.triggerType = 'cron';
  config.mode = 'incremental';
} else {
  config.triggerType = 'manual';
  config.mode = 'full';
  config.clearFirst = true;
}

return [{ json: config }];

// Scanner le dossier WIDIP et detecter les projets
const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

const config = $input.first().json;
const basePath = config.sourcePath;
const supportedExtensions = config.supportedExtensions;
const customCategory = config.customCategory;
const conversationId = config.conversationId;
const userId = config.userId;

function getFileHash(filePath) {
  try {
    const content = fs.readFileSync(filePath);
    return crypto.createHash('md5').update(content).digest('hex');
  } catch (err) {
    return null;
  }
}

// Detection des categories basee sur l'arborescence WIDIP
function getCategory(filePath) {
  if (customCategory) return customCategory;
  const lowerPath = filePath.toLowerCase();

  // Detection par projet WIDIP
  if (lowerPath.includes('/00_global/')) return '00_GLOBAL';
  if (lowerPath.includes('/01_wibot/')) return '01_WIBOT';
  if (lowerPath.includes('/02_mcp_server/')) return '02_MCP_SERVER';
  if (lowerPath.includes('/03_safeguard/')) return '03_SAFEGUARD';
  if (lowerPath.includes('/04_workflow_proactif')) return '04_PROACTIF_OBSERVIUM';
  if (lowerPath.includes('/05_workflow_assist')) return '05_ASSIST_TICKET';
  if (lowerPath.includes('/06_systeme_rag/')) return '06_SYSTEME_RAG';
  if (lowerPath.includes('/07_projet_lorraine/')) return '07_PROJET_LORRAINE';
  if (lowerPath.includes('/08_projet_liliana/')) return '08_PROJET_LILIANA';
  if (lowerPath.includes('/09_projet_maxime/')) return '09_PROJET_MAXIME';
  if (lowerPath.includes('/sandbox/')) return 'SANDBOX';

  // Detection par type de fichier
  if (lowerPath.includes('/documentation/')) return 'documentation';
  if (lowerPath.includes('/workflows/')) return 'workflow';
  if (lowerPath.includes('/src/')) return 'code';

  return 'general';
}

// Dossiers a exclure
const excludeDirs = ['node_modules', '_archive', '.git', '__pycache__', 'dist', 'build'];

function scanDirectory(dirPath, results = []) {
  try {
    if (!fs.existsSync(dirPath)) {
      console.log('Directory does not exist:', dirPath);
      return results;
    }

    const items = fs.readdirSync(dirPath);

    for (const item of items) {
      if (item.startsWith('.')) continue;
      if (excludeDirs.includes(item.toLowerCase())) continue;

      const fullPath = path.join(dirPath, item);
      const stat = fs.statSync(fullPath);

      if (stat.isDirectory()) {
        scanDirectory(fullPath, results);
      } else if (stat.isFile()) {
        const ext = path.extname(item).toLowerCase();
        if (supportedExtensions.includes(ext)) {
          const fileData = {
            path: fullPath,
            filename: item,
            extension: ext,
            size: stat.size,
            modified: stat.mtime.toISOString(),
            hash: getFileHash(fullPath),
            category: getCategory(fullPath),
            relativePath: fullPath.replace(basePath, '')
          };
          if (conversationId) fileData.conversation_id = conversationId;
          if (userId) fileData.user_id = userId;
          results.push(fileData);
        }
      }
    }
  } catch (err) {
    console.error('Error scanning:', dirPath, err.message);
  }
  return results;
}

const files = scanDirectory(basePath);

if (files.length === 0) {
  return [{
    json: {
      error: true,
      message: 'Aucun fichier trouve dans ' + basePath,
      scannedPath: basePath
    }
  }];
}

return files.map(f => ({ json: f }));

// Extraction de texte pour fichiers simples (MD, TXT, HTML, PY, TS, JS)
const fs = require('fs');
const items = $input.all();
const results = [];

for (const item of items) {
  const filePath = item.json.path;
  try {
    let content = fs.readFileSync(filePath, 'utf-8');

    // Nettoyer le contenu HTML si necessaire
    if (item.json.extension === '.html' || item.json.extension === '.htm') {
      content = content
        .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
        .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
        .replace(/<[^>]+>/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();
    }

    // Ajouter contexte pour fichiers code
    if (['.py', '.ts', '.js'].includes(item.json.extension)) {
      content = `[Code ${item.json.extension.toUpperCase()}: ${item.json.filename}]\n\n${content}`;
    }

    results.push({
      json: {
        ...item.json,
        content: content,
        contentLength: content.length,
        extractedAt: new Date().toISOString()
      }
    });
  } catch (err) {
    results.push({
      json: {
        ...item.json,
        error: true,
        errorMessage: err.message
      }
    });
  }
}

return results;

const items = $input.all();
const scanData = $('Switch by Extension').all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const originalData = scanData[i]?.json || {};

  let content = '';
  if (item.json.data) {
    content = item.json.data;
  } else if (item.json.text) {
    content = item.json.text;
  } else if (typeof item.json === 'string') {
    content = item.json;
  }

  if (!content || content.length < 10) {
    content = `[PDF: ${originalData.filename}] Document PDF - Extraction limitee. Taille: ${originalData.size} bytes.`;
  }

  results.push({
    json: {
      ...originalData,
      content: content.substring(0, 50000),
      contentLength: content.length,
      extractionMethod: 'n8n-native-pdf',
      extractedAt: new Date().toISOString()
    }
  });
}

return results;

const items = $input.all();
const scanData = $('Switch by Extension').all();

const fileGroups = {};

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const originalData = scanData[i % scanData.length]?.json || {};
  const filename = originalData.filename || 'unknown';

  if (!fileGroups[filename]) {
    fileGroups[filename] = {
      originalData,
      rows: []
    };
  }
  fileGroups[filename].rows.push(item.json);
}

const results = [];
for (const [filename, group] of Object.entries(fileGroups)) {
  let content = `[Excel: ${filename}]\n\n`;

  if (group.rows.length > 0) {
    const columns = Object.keys(group.rows[0]);
    content += `Colonnes: ${columns.join(', ')}\n`;
    content += `Nombre de lignes: ${group.rows.length}\n\n`;

    for (const row of group.rows.slice(0, 500)) {
      const rowText = columns.map(col => `${col}: ${row[col] || ''}`).join(' | ');
      content += rowText + '\n';
    }

    if (group.rows.length > 500) {
      content += `\n... et ${group.rows.length - 500} lignes supplementaires`;
    }
  }

  results.push({
    json: {
      ...group.originalData,
      content: content.substring(0, 50000),
      contentLength: content.length,
      extractionMethod: 'n8n-native-excel',
      rowCount: group.rows.length,
      extractedAt: new Date().toISOString()
    }
  });
}

return results.length > 0 ? results : [{ json: { error: true, message: 'No Excel data' }}];

const items = $input.all();
const scanData = $('Switch by Extension').all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const originalData = scanData[i]?.json || {};

  let content = '';
  if (item.json.data) {
    content = item.json.data;
  } else if (item.json.text) {
    content = item.json.text;
  } else if (typeof item.json === 'string') {
    content = item.json;
  }

  if (!content || content.length < 10) {
    content = `[Word: ${originalData.filename}] Document Word - Extraction limitee. Taille: ${originalData.size} bytes.`;
  }

  results.push({
    json: {
      ...originalData,
      content: content.substring(0, 50000),
      contentLength: content.length,
      extractionMethod: 'n8n-native-docx',
      extractedAt: new Date().toISOString()
    }
  });
}

return results;

const fs = require('fs');
const items = $input.all();
const results = [];

for (const item of items) {
  const filePath = item.json.path;
  try {
    const content = fs.readFileSync(filePath, 'utf-8');

    const lines = content.split('\n').filter(l => l.trim());
    const headers = lines[0] ? lines[0].split(/[,;\t]/) : [];

    let textContent = `[CSV: ${item.json.filename}]\n`;
    textContent += `Colonnes: ${headers.join(', ')}\n`;
    textContent += `Nombre de lignes: ${lines.length}\n\n`;
    textContent += `Donnees:\n${content.substring(0, 30000)}`;

    results.push({
      json: {
        ...item.json,
        content: textContent,
        contentLength: textContent.length,
        extractionMethod: 'csv-text',
        extractedAt: new Date().toISOString(),
        rowCount: lines.length,
        columns: headers
      }
    });
  } catch (err) {
    results.push({
      json: {
        ...item.json,
        content: `[CSV Error: ${item.json.filename}] ${err.message}`,
        error: true,
        errorMessage: err.message
      }
    });
  }
}

return results;

const fs = require('fs');
const items = $input.all();
const results = [];

for (const item of items) {
  const filePath = item.json.path;
  try {
    const rawContent = fs.readFileSync(filePath, 'utf-8');
    const jsonData = JSON.parse(rawContent);

    let textContent = `[JSON: ${item.json.filename}]\n`;

    function extractText(obj, prefix = '') {
      let text = '';
      if (typeof obj === 'string') {
        text += obj + ' ';
      } else if (Array.isArray(obj)) {
        obj.forEach((item, i) => {
          text += extractText(item, `${prefix}[${i}]`);
        });
      } else if (typeof obj === 'object' && obj !== null) {
        for (const key of Object.keys(obj)) {
          text += `${key}: `;
          text += extractText(obj[key], `${prefix}.${key}`);
        }
      } else if (obj !== null && obj !== undefined) {
        text += String(obj) + ' ';
      }
      return text;
    }

    textContent += extractText(jsonData);

    results.push({
      json: {
        ...item.json,
        content: textContent.substring(0, 50000),
        contentLength: textContent.length,
        extractionMethod: 'json-flatten',
        extractedAt: new Date().toISOString()
      }
    });
  } catch (err) {
    results.push({
      json: {
        ...item.json,
        content: `[JSON Error: ${item.json.filename}] ${err.message}`,
        error: true,
        errorMessage: err.message
      }
    });
  }
}

return results;

const items = $input.all();
return items.map(item => {
  const doc = {
    content: item.json.content,
    source: item.json.filename,
    category: item.json.category,
    project: item.json.category,
    path: item.json.relativePath || item.json.path,
    extension: item.json.extension,
    hash: item.json.hash
  };
  if (item.json.conversation_id) doc.conversation_id = item.json.conversation_id;
  if (item.json.user_id) doc.user_id = item.json.user_id;
  return { json: doc };
});

const stats = $input.first().json;
const config = $('Detect Mode & Config').first().json;

return [{
  json: {
    success: true,
    message: 'Ingestion RAG WIDIP terminee avec succes',
    stats: {
      totalVectors: parseInt(stats.total_vectors) || 0,
      uniqueSources: parseInt(stats.unique_sources) || 0,
      uniqueCategories: parseInt(stats.unique_categories) || 0,
      uniqueProjects: parseInt(stats.unique_projects) || 0
    },
    config: {
      mode: config.mode,
      triggerType: config.triggerType,
      sourcePath: config.sourcePath,
      startedAt: config.startedAt,
      completedAt: new Date().toISOString()
    }
  }
}];
